// pages/Home.js (or wherever Home lives)
import React, { useEffect, useState } from "react";
import Posts from "../components/Posts";

function Home() {
  const [appState, setAppState] = useState({
    loading: true,
    posts: null,
  });

  useEffect(() => {
    const timer = setTimeout(() => {
      fetch("https://jsonplaceholder.typicode.com/posts")
        .then((res) => res.json())
        .then((posts) => {
          setAppState({ loading: false, posts: posts.slice(0, 5) });
        })
        .catch(() => {
          // optional: stop spinner even if it fails
          setAppState({ loading: false, posts: [] });
        });
    }, 1500); // 1.5s simulated delay

    return () => clearTimeout(timer);
  }, []);

  return <Posts isLoading={appState.loading} posts={appState.posts} />;
}

export default Home;
